server_data_table_display <- function(id, input_data_reactive) {
  moduleServer(id, function(input, output, session) {
    processed_data <- reactiveVal()

    # Debounce the R code input to avoid rapid re-evaluations
    debounced_code_input <- debounce(reactive(input$code_input), 1000)

    observe({ # Unified observer for code changes or data changes
      current_code <- debounced_code_input()
      current_data <- input_data_reactive()
      
      # Don't req(current_data) here - our code templates handle NULL data gracefully
      
      # Only proceed if code is non-empty, otherwise might show raw data
      # Or, if code is empty, could default to showing current_data directly as DT
      if (is.null(current_code) || current_code == "") {
          # Default behavior if no code: render data as a basic DT table
          if (!is.null(current_data) && (is.data.frame(current_data) || is.data.table(current_data))) {
            processed_data(datatable(current_data, options = list(scrollX = TRUE, pageLength = 5, lengthMenu = c(5, 10, 25, 50)), rownames = FALSE, filter = 'top', class = 'compact stripe hover'))
          } else {
            # Handle NULL data case even when no custom code
            processed_data(data.frame(Message = "No data available") |> datatable(options = list(dom = 't', ordering = FALSE), rownames = FALSE, class = 'compact'))
          }
          return()
      }

      spsComps::shinyCatch({
        # Ensure 'df' is available in the eval environment
        env <- new.env(parent = .GlobalEnv) # Safer environment
        env$df <- current_data
        # Make essential functions available for data table display code
        env$datatable <- DT::datatable
        env$renderPrint <- shiny::renderPrint
        env$skim <- skimr::skim  # Use fully qualified name
        env$setDT <- data.table::setDT
        env$data.frame <- data.frame
        env$is.data.table <- data.table::is.data.table
        env$is.data.frame <- is.data.frame
        env$is.null <- is.null
        env$nrow <- nrow
        env$ncol <- ncol
        env$names <- names
        env$paste <- paste
        env$c <- c
        env$library <- library  # Allow library calls in eval code
        env$require <- require   # Allow require calls in eval code
        
        evaluated_output <- eval(parse(text = current_code), envir = env)
        processed_data(evaluated_output)
      })
    }) |> bindEvent(list(debounced_code_input(), input_data_reactive(), input$apply_code), ignoreNULL = FALSE, ignoreInit = FALSE)


    output$data_table <- renderUI({
      req(processed_data()) # Ensure processed_data is available
      tagList(processed_data()) # Wrap in tagList in case it's multiple UI elements
    })
  })
}

# --- Server Module for Plotter ---
server_plotter <- function(id, combined_data_reactive, main_session_input = NULL) { # Added main_session_input
  moduleServer(id, function(input, output, session) {
    ns <- session$ns

    # Tab renaming functionality for plotter (same as importer)
    plot_rename_trigger <- reactiveVal(NULL)

    # Auto-apply plot name when input changes (no button needed)
    observeEvent(input$plot_name, {
      req(input$plot_name)
      new_name <- trimws(input$plot_name)

      cat("=== PLOT NAME AUTO-APPLY ===\n")
      cat("Module ID:", id, "\n")
      cat("New name:", new_name, "\n")

      if (new_name == "") {
        return() # Don't apply empty names
      }

      # Set the reactive value to trigger parent's observer
      current_title <- id # Use the module ID as current title
      plot_rename_trigger(list(
        moduleId = id,
        newName = new_name,
        currentTitle = current_title,
        timestamp = Sys.time() # Ensure trigger fires even with same name
      ))

      cat("Plot rename trigger set automatically\n")
      showNotification(paste("Plot renamed to:", new_name), type = "message", duration = 2)
    }, ignoreInit = TRUE) # Only trigger on user changes, not initial load

    # --- Plot-specific Data Processing ---
    df_plot_data_processed <- reactiveVal()
    r_code_plot_process_reactive <- debounce(reactive(input$r_code_plot_process), 500)
    ace_server_functions(ns("r_code_plot_process"))

    # Multiple ace editors for different plot components
    r_code_plot_text_reactive <- debounce(reactive(input$r_code_plot_text), 500)
    r_code_plot_static_reactive <- debounce(reactive(input$r_code_plot_static), 500)
    r_code_plot_interactive_reactive <- debounce(reactive(input$r_code_plot_interactive), 500)
    r_code_plot_table_reactive <- debounce(reactive(input$r_code_plot_table), 500)
    r_code_plot_final_reactive <- debounce(reactive(input$r_code_plot_final), 500)

    # Ace server functions for each editor
    ace_server_functions(ns("r_code_plot_text"))
    ace_server_functions(ns("r_code_plot_static"))
    ace_server_functions(ns("r_code_plot_interactive"))
    ace_server_functions(ns("r_code_plot_table"))
    ace_server_functions(ns("r_code_plot_final"))

    # Update source filter checkboxes when combined data changes
    observe({
      req(combined_data_reactive())

      combined_df <- combined_data_reactive()
      if (!is.null(combined_df) && nrow(combined_df) > 0 && 'source_importer_id' %in% names(combined_df)) {
        # Get unique source IDs
        unique_sources <- unique(combined_df$source_importer_id)

        if (length(unique_sources) > 0) {
          # Create mapping from module IDs to friendly names
          source_labels <- sapply(unique_sources, function(source_id) {
            # Convert data_import_module_1 to "Data Import 1"
            if (grepl("^data_import_module_", source_id)) {
              tab_number <- gsub("data_import_module_", "", source_id)
              paste("Data Import", tab_number)
            } else {
              # Fallback for other naming patterns
              source_id
            }
          })

          # Generate checkboxes
          checkbox_list <- lapply(seq_along(unique_sources), function(i) {
            source_id <- unique_sources[i]
            label <- source_labels[i]

            div(class = "form-check form-check-inline",
              tags$input(
                type = "checkbox",
                class = "form-check-input",
                id = session$ns(paste0("source_", source_id)),
                value = source_id,
                checked = "checked" # Default to all sources selected
              ),
              tags$label(
                class = "form-check-label small",
                `for` = session$ns(paste0("source_", source_id)),
                label
              )
            )
          })

          # Update the UI
          output$source_filter_checkboxes <- renderUI({
            tagList(checkbox_list)
          })
        }
      } else {
        # No data available
        output$source_filter_checkboxes <- renderUI({
          p(class = "text-muted small", "No data sources available yet")
        })
      }
    })

    # Process data when button is clicked
    observeEvent(input$data_render, {
      req(combined_data_reactive())

      spsComps::shinyCatch({
        # Get the combined data
        combined_df <- data.table::copy(combined_data_reactive())

        if (is.null(combined_df) || nrow(combined_df) == 0) {
          showNotification("No data available to process", type = "warning")
          return()
        }

        # Apply source filtering
        if ('source_importer_id' %in% names(combined_df)) {
          # Get all unique sources
          unique_sources <- unique(combined_df$source_importer_id)

          # Check which sources are selected
          selected_sources <- c()
          for (source_id in unique_sources) {
            checkbox_id <- paste0("source_", source_id)
            if (!is.null(input[[checkbox_id]]) && input[[checkbox_id]]) {
              selected_sources <- c(selected_sources, source_id)
            }
          }

          # Filter data by selected sources
          if (length(selected_sources) > 0) {
            combined_df <- combined_df[combined_df$source_importer_id %in% selected_sources, ]
          } else {
            # If no sources selected, show warning and use all data
            showNotification("No sources selected - using all data", type = "warning")
          }
        }

        # Apply sampling if specified
        if (!is.null(input$sample_n_plot) && input$sample_n_plot > 0 && input$sample_n_plot < 1) {
          n_sample <- floor(nrow(combined_df) * input$sample_n_plot)
          if (n_sample > 0) {
            combined_df <- combined_df[sample(nrow(combined_df), n_sample), ]
          }
        }

        # Environment for plot data processing
        env <- new.env(parent = .GlobalEnv)
        env$df <- as.data.table(combined_df) # Start with filtered and sampled data
        env$input <- input # Expose module's input for filtering etc.
        env$filter_in <- filter_in
        env$filter_out <- filter_out
        env$rname <- rname
        env$str_extract <- stringr::str_extract
        env$sample <- sample # base R sample

        # Add code sharing functions
        env$get_plotter_code <- get_plotter_code
        env$get_importer_code <- get_importer_code
        env$main_session_input <- main_session_input

        processed_df <- eval(parse(text = r_code_plot_process_reactive()), envir = env)
        df_plot_data_processed(processed_df)

        # Update aesthetics choices based on processed data columns
        if (!is.null(processed_df) && ncol(processed_df) > 0) {
          column_choices <- c("None" = "null", setNames(names(processed_df), names(processed_df)))

          updateSelectInput(session, "plot_color", choices = column_choices, selected = if("series" %in% names(processed_df)) "series" else "null")
          updateSelectInput(session, "plot_linetype", choices = column_choices, selected = "null")
        }

        showNotification("Plot-specific data processing complete.", type = "message")
      })
    })

    # Display tables for plot data
    server_data_table_display(
      "plot_data_table_input_data",
      reactive(combined_data_reactive()) # Show the raw input to this plotter
    )
    server_data_table_display(
      "plot_data_table_input_data_summary",
      reactive(combined_data_reactive()) # Show summary of raw input data
    )
    server_data_table_display(
      "plot_data_table_modified_data",
      reactive(df_plot_data_processed()) # Show data after plot-specific processing
    )
    server_data_table_display(
      "plot_data_table_modified_data_summary",
      reactive(df_plot_data_processed()) # Summary of processed data
    )

    # --- Plot Rendering Logic ---
    plot_object_reactive <- reactiveVal(NULL)

    observeEvent(input$plot_render, {
      spsComps::shinyCatch({
        req(df_plot_data_processed()) # Requires data processed for *this* plot

        # Environment for plot generation
        env <- new.env(parent = .GlobalEnv)
        env$df <- df_plot_data_processed()
        env$input <- input # Expose module's input for titles, labels, etc.

        # Check if ggplot2 can load properly
        ggplot2_available <- tryCatch({
          # Test if we can actually load ggplot2 and its dependencies
          library(ggplot2, quietly = TRUE)
          # Test a simple ggplot operation
          test_plot <- ggplot(data.frame(x=1, y=1), aes(x=x, y=y)) + geom_point()
          TRUE
        }, error = function(e) {
          cat("ggplot2 loading failed:", e$message, "\n")
          FALSE
        })

        if (ggplot2_available) {
          # Add ggplot2 functions - full version
          env$ggplot <- ggplot2::ggplot
          env$geom_line <- ggplot2::geom_line
          env$geom_point <- ggplot2::geom_point
          env$geom_ribbon <- ggplot2::geom_ribbon
          env$aes <- ggplot2::aes
          env$labs <- ggplot2::labs
          env$ggtitle <- ggplot2::ggtitle # Deprecated, use labs(title=)
          env$xlab <- ggplot2::xlab # Deprecated
          env$ylab <- ggplot2::ylab # Deprecated
          env$theme_bw <- ggplot2::theme_bw
          env$facet_wrap <- ggplot2::facet_wrap
          env$ggplot2_available <- TRUE
        } else {
          # Create fallback functions that use base R plots or plotly
          env$ggplot2_available <- FALSE
          env$ggplot <- function(data = NULL, mapping = NULL, ...) {
            warning("ggplot2 not available - use plotly functions instead")
            return(list(data = data, mapping = mapping, type = "ggplot_fallback"))
          }
          env$geom_line <- function(...) {
            warning("ggplot2 not available - use plotly::add_lines() instead")
            return(list(type = "geom_fallback"))
          }
          env$geom_point <- function(...) {
            warning("ggplot2 not available - use plotly::add_markers() instead")
            return(list(type = "geom_fallback"))
          }
          env$geom_ribbon <- function(...) {
            warning("ggplot2 not available - use plotly::add_ribbons() instead")
            return(list(type = "geom_fallback"))
          }
          env$aes <- function(...) {
            warning("ggplot2 not available - define aesthetics directly in plotly")
            return(list(type = "aes_fallback"))
          }
          env$labs <- function(...) {
            warning("ggplot2 not available - use plotly::layout() instead")
            return(list(type = "labs_fallback"))
          }
          env$ggtitle <- function(...) {
            warning("ggplot2 not available - use plotly::layout(title = ...) instead")
            return(list(type = "title_fallback"))
          }
          env$xlab <- function(...) {
            warning("ggplot2 not available - use plotly::layout(xaxis = list(title = ...)) instead")
            return(list(type = "xlab_fallback"))
          }
          env$ylab <- function(...) {
            warning("ggplot2 not available - use plotly::layout(yaxis = list(title = ...)) instead")
            return(list(type = "ylab_fallback"))
          }
          env$theme_bw <- function(...) {
            warning("ggplot2 not available - use plotly styling instead")
            return(list(type = "theme_fallback"))
          }
          env$facet_wrap <- function(...) {
            warning("ggplot2 not available - create subplots with plotly::subplot() instead")
            return(list(type = "facet_fallback"))
          }
        }

        # Add scattermore with safe loading
        tryCatch({
          env$geom_scattermore <- scattermore::geom_scattermore
        }, error = function(e) {
          env$geom_scattermore <- function(...) {
            warning("scattermore not available - use plotly::add_markers() for large datasets")
            return(list(type = "scattermore_fallback"))
          }
        })

        env$str_sub <- stringr::str_sub
        env$ggplotly <- plotly::ggplotly
        env$plot_ly <- plotly::plot_ly
        env$add_trace <- plotly::add_trace
        env$add_lines <- plotly::add_lines
        env$add_markers <- plotly::add_markers
        env$add_ribbons <- plotly::add_ribbons
        env$layout <- plotly::layout
        env$renderPlot <- shiny::renderPlot # For static ggplot
        env$renderPlotly <- plotly::renderPlotly # For plotly/ggplotly
        env$renderPrint <- shiny::renderPrint # For text output
        env$renderUI <- shiny::renderUI # For flexible output
        env$datatable <- DT::datatable # For table output
        env$tagList <- shiny::tagList # For multiple UI elements
        env$div <- shiny::div # For HTML containers
        env$h3 <- shiny::h3 # For headings
        env$h4 <- shiny::h4 # For headings
        env$p <- shiny::p # For paragraphs
        env$copy <- data.table::copy # For data.table copying
        env$setDT <- data.table::setDT # For converting to data.table

        # Add code sharing functions
        env$get_plotter_code <- get_plotter_code
        env$get_importer_code <- get_importer_code
        env$main_session_input <- main_session_input

        # Combine all the code sections
        combined_code <- paste(
          "# Text code section:",
          paste("text_code <- '", gsub("'", "\\\\'", r_code_plot_text_reactive()), "'", sep=""),
          "",
          "# Static code section:",
          paste("static_code <- '", gsub("'", "\\\\'", r_code_plot_static_reactive()), "'", sep=""),
          "",
          "# Interactive code section:",
          paste("interactive_code <- '", gsub("'", "\\\\'", r_code_plot_interactive_reactive()), "'", sep=""),
          "",
          "# Table code section:",
          paste("table_code <- '", gsub("'", "\\\\'", r_code_plot_table_reactive()), "'", sep=""),
          "",
          "# Final conditional code:",
          r_code_plot_final_reactive(),
          sep = "\n"
        )

        plot_result <- eval(parse(text = combined_code), envir = env)
        plot_object_reactive(plot_result)
        showNotification("Plot generated successfully.", type = "message")
      })
    })

    # Flexible plot output using renderUI
    output$plot_output <- renderUI({
      req(plot_object_reactive())

      plot_obj <- plot_object_reactive()

      # Handle different types of output flexibly
      if (inherits(plot_obj, "htmlwidget")) {
        # Plotly or other HTML widgets
        plot_obj
      } else if (inherits(plot_obj, "ggplot")) {
        # ggplot objects - render as plot
        renderPlot({
          plot_obj
        }, height = 600)
      } else if (inherits(plot_obj, "datatables")) {
        # DataTable objects
        plot_obj
      } else if (is.list(plot_obj) && all(sapply(plot_obj, function(x) inherits(x, "shiny.tag") || inherits(x, "shiny.tag.list")))) {
        # List of UI elements
        tagList(plot_obj)
      } else if (inherits(plot_obj, "shiny.render.function")) {
        # Already a render function (renderPrint, etc.)
        plot_obj
      } else {
        # Fallback: try to render as print output
        renderPrint({
          plot_obj
        })
      }
    })

    # Download handler for plots
    output$download_output <- downloadHandler(
      filename = function() {
        plot_name <- if (!is.null(input$plot_name) && nzchar(input$plot_name)) {
          gsub("[^a-zA-Z0-9_.-]", "_", input$plot_name)
        } else {
          id
        }

        if (input$plot_type == "dynamic") {
          paste0(plot_name, "_", Sys.Date(), ".html")
        } else if (input$plot_type == "static") {
          paste0(plot_name, "_", Sys.Date(), ".png")
        } else {
          paste0(plot_name, "_", Sys.Date(), ".txt")
        }
      },
      content = function(file) {
        req(plot_object_reactive())

        plot_obj <- plot_object_reactive()

        if (input$plot_type == "dynamic" && inherits(plot_obj, "htmlwidget")) {
          htmlwidgets::saveWidget(plot_obj, file, selfcontained = TRUE)
        } else if (input$plot_type == "static" && inherits(plot_obj, "ggplot")) {
          ggsave(file, plot_obj, device = "png", width = 12, height = 8, dpi = 300)
        } else {
          # Fallback: save as text
          writeLines(capture.output(print(plot_obj)), file)
        }
      }
    )

    # Return the plot reactive and rename trigger for parent access
    list(
      plot = reactive(plot_object_reactive()),
      plot_rename_trigger = plot_rename_trigger
    )
  })
}

# r_code_.R: Contains default R code strings for Ace editors

# --- For Data Import Pre-processing (ui_data_importer) ---
r_code_data_pre_process <- "# R code to read and initially process a single file.
# Available variables:
# - file_path: Full path to the uploaded file.
# - file_name: Original name of the file.
# - n_every: Value from 'Read every Nth row' input (0 or 1 means all).
# - skip_rows: Value from 'Skip N rows at start' input.
# - showNotification(message, type): Function to display messages.
# Required output: A data.table or data.frame. Return NULL on error.

# Example:
# showNotification(paste('Processing:', file_name), type='message')

# Determine file extension
ext <- tools::file_ext(tolower(file_name)) # Use tolower for consistency

# Define read functions in a list for clarity
read_functions <- list(
  'csv' = function(fp, sr) data.table::fread(fp, skip = sr),
  'csv.gz' = function(fp, sr) data.table::fread(cmd = paste('gunzip -c', shQuote(fp)), skip = sr), # More robust for .csv.gz
  'gz' = function(fp, sr) data.table::fread(cmd = paste('gunzip -c', shQuote(fp)), skip = sr), # Assumes text content after gunzip
  'xlsx' = function(fp, sr) data.table::as.data.table(readxl::read_excel(fp, skip = sr)),
  'fst' = function(fp, sr) data.table::setDT(fst::read_fst(fp)), # FST doesn't typically have skip
  'parquet' = function(fp, sr) data.table::setDT(nanoparquet::read_parquet(fp)) # Parquet doesn't typically have skip
)

df <- NULL # Initialize df

if (ext %in% names(read_functions)) {
  df <- tryCatch({
    read_functions[[ext]](file_path, skip_rows)
  }, error = function(e) {
    showNotification(paste('Error reading', file_name, ':', e$message), type = 'error')
    return(NULL)
  })
} else if (ext == 'tdms') {
  showNotification('TDMS files are not directly supported. Please convert to a supported format (e.g., CSV, Parquet) first.', type = 'error', duration=10)
  return(NULL)
} else {
  showNotification(paste0('Unsupported file type: .', ext, '. Please use CSV, XLSX, FST, or Parquet.'), type = 'warning', duration=10)
  return(NULL)
}

if (is.null(df) || !is.data.frame(df)) {
  # showNotification(paste('Failed to read or convert', file_name, 'to a data frame.'), type = 'error')
  return(NULL)
}

# Ensure it's a data.table
data.table::setDT(df)

# Check row count
if (nrow(df) == 0) {
  showNotification(paste('File', file_name, 'is empty or resulted in zero rows after reading.'), type = 'warning')
  return(NULL) # Or return df if empty data.tables are acceptable downstream
}

# Downsample if n_every is greater than 1 (0 or 1 means read all relevant rows already)
# The 'n_every' input is now 'Read every Nth row (0=all)'
# So if n_every > 1, we sample. If n_every is 0 or 1, we take all.
if (exists('n_every') && is.numeric(n_every) && n_every > 1 && nrow(df) > n_every) {
  df <- df[seq(1, .N, by = as.integer(n_every))]
  showNotification(paste('Downsampled', file_name, 'to every', n_every, 'th row.'), type='message')
}

# Add file_name column for tracking provenance
df[, file_name_source := file_name] # Use a distinct name to avoid conflict if 'file_name' is a data column

df # Return the processed data.table
"

# --- For Data Import Post-processing (ui_data_importer) ---
r_code_data_processing <- "# R code to transform the data from a single file AFTER pre-processing.
# Available variables:
# - df: The data.table from the pre-processing step for the current file.
# - input_filter_in_1, input_filter_out_1, input_rename_1, input_date_format: Values from UI controls.
# - showNotification(message, type): Function.
# - filter_in(), filter_out(), rname(): Helper functions.
# Required output: A data.table.

# Ensure df is a data.table
setDT(df)

# Rename the first column to 'timestamp' if it's not already named so.
# This is a common operation but make it conditional.
if (ncol(df) > 0 && names(df)[1] != 'timestamp') {
  setnames(df, old = names(df)[1], new = 'timestamp', skip_absent=TRUE)
}

# Identify potential measurement variables (assuming 'timestamp' and 'file_name_source' are ID vars)
id_vars <- c('timestamp', 'file_name_source')
measure_vars <- setdiff(names(df), id_vars)

if (length(measure_vars) == 0) {
  showNotification('No measurement variables found after identifying id_vars. Check column names.', type='warning')
  return(df) # Or NULL if this is an error state
}

# Convert all potential measure_vars to numeric. Handle errors gracefully.
for (m_var in measure_vars) {
  if (m_var %in% names(df)) {
    # Try conversion, keep original on failure or make NA
    original_class <- class(df[[m_var]])[1]
    df[, (m_var) := suppressWarnings(as.numeric(get(m_var)))]
    if (all(is.na(df[[m_var]])) && original_class != 'numeric') {
        # showNotification(paste('Column', m_var, 'could not be fully converted to numeric. Check data.'), type='warning')
    }
  }
}

# Melt data to long format
df_long <- melt(df, 
                id.vars = id_vars, 
                measure.vars = measure_vars, 
                variable.name = 'series', 
                value.name = 'value',
                na.rm = TRUE) # Remove rows where 'value' became NA after melt (e.g. from failed as.numeric)

if (nrow(df_long) == 0) {
    showNotification('Data became empty after melting. Check numeric conversions and series.', type='warning')
    return(data.table()) # Return empty DT
}


# Parse timestamp using the format from UI. Be robust.
# input_date_format is available. Default 'ymd HMS'
if ('timestamp' %in% names(df_long)) {
  current_timestamps <- df_long[['timestamp']]
  if (!is.POSIXct(current_timestamps)) { # Only parse if not already POSIXct
    parsed_ts <- tryCatch({
      lubridate::parse_date_time(as.character(current_timestamps), orders = input_date_format, quiet = TRUE)
    }, error = function(e) NULL)

    if (!is.null(parsed_ts) && !all(is.na(parsed_ts))) {
      df_long[, timestamp := parsed_ts]
    } else {
      # Attempt with fasttime as a fallback for common formats if lubridate fails broadly
      parsed_ts_ft <- tryCatch({ fasttime::fastPOSIXct(as.character(current_timestamps)) }, error = function(e) NULL)
      if(!is.null(parsed_ts_ft) && !all(is.na(parsed_ts_ft))) {
         df_long[, timestamp := parsed_ts_ft]
         showNotification('Used fallback timestamp parser for some values.', type='message')
      } else {
         showNotification(paste('Failed to parse timestamps with format:', input_date_format, '. Check format and data.'), type='warning')
      }
    }
  }
}


# Apply filters (these helpers are available in the eval environment)
if (exists('input_filter_in_1') && nzchar(input_filter_in_1)) {
  df_long <- filter_in(df_long, 'series', input_filter_in_1)
}
if (exists('input_filter_out_1') && nzchar(input_filter_out_1)) {
  df_long <- filter_out(df_long, 'series', input_filter_out_1)
}
if (exists('input_rename_1') && nzchar(input_rename_1)) {
  df_long <- rname(df_long, 'series', input_rename_1)
}

# Extract 'dut' (Device Under Test) identifier from file_name_source as an example
# This pattern '2212\\d*' might be specific to your filenames.
if ('file_name_source' %in% names(df_long)) {
    df_long[, dut := stringr::str_extract(file_name_source, '2212[^_]*')] # Example: 2212 followed by digits until an underscore
}

# showNotification(paste('Post-processing complete for data from:', df_long[1, file_name_source]), type='message')
df_long # Return the final processed data.table for this file
"

# --- For Plot-specific Data Processing (ui_plotter) ---
r_code_plot_process_template <- "# R code to process the COMBINED data specifically for THIS plot.
# Available variables:
# - df: The data.table of combined data from all importers.
# - input: Reactive list of UI inputs for THIS plotter (e.g., input$sample_n_plot, input$filter_in_plot).
# - filter_in(), filter_out(), rname(): Helper functions.
# Required output: A data.table for plotting.

# Ensure df is a data.table
setDT(df)

# Sample data if sample_n_plot is set (0 means use all data)
if (!is.null(input$sample_n_plot) && input$sample_n_plot > 0 && input$sample_n_plot < 1) {
  if (nrow(df) * input$sample_n_plot >= 1) { # Ensure sample size is at least 1
    df <- df[sample(.N, floor(input$sample_n_plot * .N))]
  }
}

# Filter series for this plot
if (!is.null(input$filter_in_files_plot) && nzchar(input$filter_in_files_plot)) {
  df <- filter_in(df, 'file_name_source', input$filter_in_files_plot)
}

if (!is.null(input$filter_out_files_plot) && nzchar(input$filter_out_files_plot)) {
  df <- filter_out(df, 'file_name_source', input$filter_out_files_plot)
}

# Filter series for this plot
if (!is.null(input$filter_in_plot) && nzchar(input$filter_in_plot)) {
  df <- filter_in(df, 'series', input$filter_in_plot)
}
if (!is.null(input$filter_out_plot) && nzchar(input$filter_out_plot)) {
  df <- filter_out(df, 'series', input$filter_out_plot)
}

# Rename series for this plot
if (!is.null(input$rename_plot) && nzchar(input$rename_plot)) {
  df <- rname(df, 'series', input$rename_plot)
}

# Source filtering - filter by selected importer sources
if ('source_importer_id' %in% names(df)) {
    # Get all unique sources in the data
    unique_sources <- unique(df$source_importer_id)

    # Check which sources are selected via checkboxes
    selected_sources <- c()
    for (source_id in unique_sources) {
        checkbox_id <- paste0('source_', source_id)
        if (!is.null(input[[checkbox_id]]) && input[[checkbox_id]]) {
            selected_sources <- c(selected_sources, source_id)
        }
    }

    # Filter data by selected sources
    if (length(selected_sources) > 0) {
        df <- df[source_importer_id %in% selected_sources]
    }
    # If no sources selected, keep all data (no filtering)
}

# Time-based filtering using separate start/end date and time inputs
if ('timestamp' %in% names(df)) {
    # Build start datetime
    if (!is.null(input$filter_time_start_date) && !is.null(input$filter_time_start_time)) {
        start_datetime <- as.POSIXct(paste(
            as.character(input$filter_time_start_date), 
            format(input$filter_time_start_time, '%H:%M:%S')
        ))

        if (!is.na(start_datetime)) {
            df <- df[timestamp >= start_datetime]
        }
    }

    # Build end datetime
    if (!is.null(input$filter_time_end_date) && !is.null(input$filter_time_end_time)) {
        end_datetime <- as.POSIXct(paste(
            as.character(input$filter_time_end_date), 
            format(input$filter_time_end_time, '%H:%M:%S')
        ))

        if (!is.na(end_datetime)) {
            df <- df[timestamp <= end_datetime]
        }
    }
}

# Extract 'dut' if not already present or if needed differently for this plot
# This might be redundant if done in main processing, but allows plot-specific override.
if (!'dut' %in% names(df) && 'file_name_source' %in% names(df)) {
    df[, dut := stringr::str_extract(file_name_source, '2212[^_]*')]
}

df # Return the data processed for this specific plot
"

# --- Separate Ace Editor Templates for Plotting ---

# Text Output Template
ggplot_text_template <- "# Text output code
# Available: df (processed data), input (UI inputs)
# Return: Any printable R output (summary, table, custom text, etc.)

# Example text outputs:
list(
  'Data Summary' = summary(df),
  'Row Count' = nrow(df),
  'Column Names' = names(df),
  'Unique Series' = if('series' %in% names(df)) unique(df$series) else 'No series column',
  'Date Range' = if('timestamp' %in% names(df)) {
    paste('From:', min(df$timestamp, na.rm=TRUE), 'To:', max(df$timestamp, na.rm=TRUE))
  } else 'No timestamp column',
  'Sample Data' = head(df, 10)
)
"

# Static Plot Template (ggplot2)
ggplot_static_template <- "# Static plot code (ggplot2)
# Available: df (processed data), input (UI inputs)
# Return: ggplot object

# Handle X-axis transformation
plot_df <- copy(df) # Work on a copy to avoid modifying original data

if ('timestamp' %in% names(plot_df)) {
  min_ts <- min(plot_df$timestamp, na.rm = TRUE)

  # Transform X-axis based on input selection
  if (!is.null(input$plot_xlabel)) {
    if (input$plot_xlabel == 'Duration Minutes') {
      plot_df[, x_axis := as.numeric(difftime(timestamp, min_ts, units = 'mins'))]
      x_label <- 'Duration (Minutes)'
    } else if (input$plot_xlabel == 'Duration Hours') {
      plot_df[, x_axis := as.numeric(difftime(timestamp, min_ts, units = 'hours'))]
      x_label <- 'Duration (Hours)'
    } else if (input$plot_xlabel == 'Duration Days') {
      plot_df[, x_axis := as.numeric(difftime(timestamp, min_ts, units = 'days'))]
      x_label <- 'Duration (Days)'
    } else if (input$plot_xlabel == 'Duration Seconds') {
      plot_df[, x_axis := as.numeric(difftime(timestamp, min_ts, units = 'secs'))]
      x_label <- 'Duration (Seconds)'
    } else {
      # Default to timestamp or custom label
      plot_df[, x_axis := timestamp]
      x_label <- input$plot_xlabel
    }
  } else {
    plot_df[, x_axis := timestamp]
    x_label <- 'Timestamp'
  }
} else {
  # No timestamp column, use first numeric column or create index
  if (ncol(plot_df) > 0) {
    plot_df[, x_axis := .I] # Use row index
    x_label <- 'Index'
  }
}

# Build aesthetic mapping
aes_mapping <- aes(x = x_axis, y = value)

# Add color mapping if specified
if (!is.null(input$plot_color) && input$plot_color != 'null' && input$plot_color %in% names(plot_df)) {
  aes_mapping$colour <- as.name(input$plot_color)
}

# Add line type mapping if specified  
if (!is.null(input$plot_linetype) && input$plot_linetype != 'null' && input$plot_linetype %in% names(plot_df)) {
  aes_mapping$linetype <- as.name(input$plot_linetype)
}

# Create plot with combined aesthetics
p <- ggplot(plot_df, aes_mapping)

# Choose geometry based on fast rendering option
if (!is.null(input$plot_vector) && input$plot_vector == TRUE) {
  p <- p + geom_scattermore(pointsize = 2)
} else {
  p <- p + geom_line()
}

# Add labels and title with proper legend names
plot_labs <- labs(
  title = if(!is.null(input$plot_title) && nzchar(input$plot_title)) input$plot_title else 'Plot Title',
  x = x_label,
  y = if(!is.null(input$plot_ylabel)) input$plot_ylabel else 'Y Axis'
)

# Add proper legend labels
if (!is.null(input$plot_color) && input$plot_color != 'null' && input$plot_color %in% names(plot_df)) {
  plot_labs$colour <- input$plot_color
}
if (!is.null(input$plot_linetype) && input$plot_linetype != 'null' && input$plot_linetype %in% names(plot_df)) {
  plot_labs$linetype <- input$plot_linetype
}

p <- p + plot_labs

