server_data_combiner <- function(id, list_of_importer_df_reactives) {
  moduleServer(id, function(input, output, session) {
    
    # Reactive values for filtering
    filter_applied <- reactiveVal(FALSE)
    filtered_data <- reactiveVal(NULL)
    
    combined_data_from_all_importers <- reactive({
      
      get_valid_df <- function(df_reactive_fn) {
        if (is.function(df_reactive_fn)) {
          df <- df_reactive_fn() # Call the reactive function
          if (!is.null(df) && (is.data.frame(df) || is.data.table(df)) && nrow(df) > 0) {
            return(as.data.table(df)) # Ensure it's a data.table
          }
        }
        return(NULL)
      }

      # Get the current list of reactive functions
      # list_of_importer_df_reactives can be either a reactive or a static list
      current_reactives <- if (is.function(list_of_importer_df_reactives)) {
        list_of_importer_df_reactives() # Call the reactive to get the list
      } else {
        list_of_importer_df_reactives # Use the static list directly
      }
      
      # Handle empty list case
      if (length(current_reactives) == 0) {
        return(NULL)
      }

      # Evaluate each reactive in the list
      evaluated_dfs_list <- lapply(current_reactives, get_valid_df)
      
      # Assign names based on the position in the original list (e.g., "importer_1", "importer_2")
      # These names will be used for the 'idcol' if they are preserved through Filter.
      # Use existing names if available, otherwise create default names
      if (is.null(names(current_reactives))) {
        names(evaluated_dfs_list) <- paste0("importer_", seq_along(evaluated_dfs_list))
      } else {
        names(evaluated_dfs_list) <- names(current_reactives)
      }
      
      # Filter out any NULLs that resulted from importers with no data or errors
      valid_dfs_to_combine <- Filter(Negate(is.null), evaluated_dfs_list)

      if (length(valid_dfs_to_combine) > 0) {
        # rbindlist will use the names of the list elements for the idcol
        tryCatch({
          # Ensure all data.tables have consistent column types
          valid_dfs_to_combine <- lapply(valid_dfs_to_combine, function(dt) {
            # Convert to data.table if not already
            dt <- as.data.table(dt)
            
            # Ensure consistent column types for common columns
            if ("timestamp" %in% names(dt)) {
              dt$timestamp <- as.POSIXct(dt$timestamp)
            }
            if ("value" %in% names(dt)) {
              dt$value <- as.numeric(dt$value)
            }
            if ("series" %in% names(dt)) {
              dt$series <- as.character(dt$series)
            }
            if ("file_name_source" %in% names(dt)) {
              dt$file_name_source <- as.character(dt$file_name_source)
            }
            
            return(dt)
          })
          
          combined_df <- data.table::rbindlist(
            valid_dfs_to_combine, 
            use.names = TRUE, 
            fill = TRUE, 
            idcol = "source_importer_id"
          )
          
          # Apply filters if they are active
          if (filter_applied()) {
            return(filtered_data())
          } else {
            return(combined_df)
          }
          
        }, error = function(e) {
          warning("Error in rbindlist: ", e$message, ". Trying with ignore.attr=TRUE")
          # Fallback: try with ignore.attr=TRUE to handle class mismatches
          tryCatch({
            combined_df <- data.table::rbindlist(
              valid_dfs_to_combine, 
              use.names = TRUE, 
              fill = TRUE, 
              idcol = "source_importer_id",
              ignore.attr = TRUE  # This handles class attribute mismatches
            )
            
            if (filter_applied()) {
              return(filtered_data())
            } else {
              return(combined_df)
            }
          }, error = function(e2) {
            warning("Even fallback rbindlist failed: ", e2$message)
            return(NULL)
          })
        })
      } else {
        NULL # Or return an empty data.table: data.table()
      }
    })
    
    # Update source filter checkboxes when combined data changes
    observe({
      base_data <- combined_data_from_all_importers()
      
      if (!is.null(base_data) && nrow(base_data) > 0 && 'source_importer_id' %in% names(base_data)) {
        # Get unique source IDs
        unique_sources <- unique(base_data$source_importer_id)
        
        if (length(unique_sources) > 0) {
          # Create mapping from module IDs to friendly names
          source_labels <- sapply(unique_sources, function(source_id) {
            # Convert data_import_module_1 to "Data Import 1"
            if (grepl("^data_import_module_", source_id)) {
              tab_number <- gsub("data_import_module_", "", source_id)
              paste("Data Import", tab_number)
            } else if (grepl("^importer_", source_id)) {
              tab_number <- gsub("importer_", "", source_id)
              paste("Importer", tab_number)
            } else {
              # Fallback for other naming patterns
              source_id
            }
          })
          
          # Generate checkboxes
          checkbox_list <- lapply(seq_along(unique_sources), function(i) {
            source_id <- unique_sources[i]
            label <- source_labels[i]
            
            div(class = "form-check form-check-inline",
              tags$input(
                type = "checkbox",
                class = "form-check-input",
                id = session$ns(paste0("source_", source_id)),
                value = source_id,
                checked = "checked" # Default to all sources selected
              ),
              tags$label(
                class = "form-check-label small",
                `for` = session$ns(paste0("source_", source_id)),
                label
              )
            )
          })
          
          # Update the UI
          output$source_filter_checkboxes <- renderUI({
            tagList(checkbox_list)
          })
        }
      } else {
        # No data available
        output$source_filter_checkboxes <- renderUI({
          p(class = "text-muted small", "No data sources available yet")
        })
      }
    })
    
    # Apply filters to combined data
    observeEvent(input$apply_combiner_filters, {
      base_data <- combined_data_from_all_importers()
      
      if (is.null(base_data)) {
        showNotification("No data available to filter", type = "warning")
        return()
      }
      
      filtered_df <- data.table::copy(base_data)
      
      tryCatch({
        # Apply source filtering using checkboxes
        if ('source_importer_id' %in% names(filtered_df)) {
          # Get all unique sources
          unique_sources <- unique(filtered_df$source_importer_id)
          
          # Check which sources are selected
          selected_sources <- c()
          for (source_id in unique_sources) {
            checkbox_id <- paste0("source_", source_id)
            if (!is.null(input[[checkbox_id]]) && input[[checkbox_id]]) {
              selected_sources <- c(selected_sources, source_id)
            }
          }
          
          # Filter data by selected sources
          if (length(selected_sources) > 0) {
            filtered_df <- filtered_df[source_importer_id %in% selected_sources]
          } else {
            # If no sources selected, show warning and use all data
            showNotification("No sources selected - using all data", type = "warning")
          }
        }
        
        # Apply series filtering - Include Series
        if (!is.null(input$combiner_filter_in_series) && input$combiner_filter_in_series != "" && "series" %in% names(filtered_df)) {
          filtered_df <- filter_in(filtered_df, 'series', input$combiner_filter_in_series)
        }
        
        # Apply series filtering - Exclude Series
        if (!is.null(input$combiner_filter_out_series) && input$combiner_filter_out_series != "" && "series" %in% names(filtered_df)) {
          filtered_df <- filter_out(filtered_df, 'series', input$combiner_filter_out_series)
        }
        
        # Apply file filtering - Include Files
        if (!is.null(input$combiner_filter_in_files) && input$combiner_filter_in_files != "" && "file_name_source" %in% names(filtered_df)) {
          filtered_df <- filter_in(filtered_df, 'file_name_source', input$combiner_filter_in_files)
        }
        
        # Apply file filtering - Exclude Files
        if (!is.null(input$combiner_filter_out_files) && input$combiner_filter_out_files != "" && "file_name_source" %in% names(filtered_df)) {
          filtered_df <- filter_out(filtered_df, 'file_name_source', input$combiner_filter_out_files)
        }
        
        # Apply timestamp filtering
        if ('timestamp' %in% names(filtered_df)) {
          # Start time filtering
          if (!is.null(input$combiner_filter_start_enabled) && input$combiner_filter_start_enabled) {
            if (!is.null(input$combiner_filter_start_date) && !is.null(input$combiner_filter_start_time)) {
              start_datetime <- as.POSIXct(paste(
                as.character(input$combiner_filter_start_date), 
                format(input$combiner_filter_start_time, '%H:%M:%S')
              ))
              
              if (!is.na(start_datetime)) {
                filtered_df <- filtered_df[timestamp >= start_datetime]
              }
            }
          }
          
          # End time filtering
          if (!is.null(input$combiner_filter_end_enabled) && input$combiner_filter_end_enabled) {
            if (!is.null(input$combiner_filter_end_date) && !is.null(input$combiner_filter_end_time)) {
              end_datetime <- as.POSIXct(paste(
                as.character(input$combiner_filter_end_date), 
                format(input$combiner_filter_end_time, '%H:%M:%S')
              ))
              
              if (!is.na(end_datetime)) {
                filtered_df <- filtered_df[timestamp <= end_datetime]
              }
            }
          }
        }
        
        filtered_data(filtered_df)
        filter_applied(TRUE)
        
        showNotification(paste("Filters applied. Showing", nrow(filtered_df), "of", nrow(base_data), "rows"), type = "message")
        
      }, error = function(e) {
        showNotification(paste("Filter error:", e$message), type = "error")
        return()
      })
    })
    
    # Clear filters
    observeEvent(input$clear_combiner_filters, {
      filter_applied(FALSE)
      filtered_data(NULL)
      
      # Clear input fields
      updateTextInput(session, "combiner_filter_in_series", value = "")
      updateTextInput(session, "combiner_filter_out_series", value = "")
      updateTextInput(session, "combiner_filter_in_files", value = "")
      updateTextInput(session, "combiner_filter_out_files", value = "")
      
      # Clear timestamp filtering
      updateCheckboxInput(session, "combiner_filter_start_enabled", value = FALSE)
      updateCheckboxInput(session, "combiner_filter_end_enabled", value = FALSE)
      updateDateInput(session, "combiner_filter_start_date", value = Sys.Date())
      updateDateInput(session, "combiner_filter_end_date", value = Sys.Date())
      
      # Reset all source checkboxes to checked
      base_data <- combined_data_from_all_importers()
      if (!is.null(base_data) && 'source_importer_id' %in% names(base_data)) {
        unique_sources <- unique(base_data$source_importer_id)
        for (source_id in unique_sources) {
          checkbox_id <- paste0("source_", source_id)
          updateCheckboxInput(session, checkbox_id, value = TRUE)
        }
      }
      
      showNotification("Filters cleared - showing all data", type = "message")
    })
    
    # Clear all data
    observeEvent(input$clear_combined_data, {
      # This is a more complex operation that would need to clear data from all importers
      # For now, we'll show a confirmation and clear filters
      showModal(modalDialog(
        title = "Clear All Data",
        "This will clear all processed data from all import modules. Are you sure?",
        footer = tagList(
          modalButton("Cancel"),
          actionButton("confirm_clear_data", "Yes, Clear All", class = "btn-danger")
        )
      ))
    })
    
    # Status display
    output$combiner_status <- renderText({
      data <- combined_data_from_all_importers()
      
      if (is.null(data)) {
        "Status: No data available"
      } else {
        total_rows <- nrow(data)
        unique_sources <- length(unique(data$source_importer_id))
        unique_files <- if ("file_name_source" %in% names(data)) {
          length(unique(data$file_name_source))
        } else {
          "N/A"
        }
        
        filter_status <- if (filter_applied()) "Filtered" else "All data"
        
        paste0(
          "Status: ", filter_status, "\n",
          "Rows: ", format(total_rows, big.mark = ","), "\n",
          "Sources: ", unique_sources, "\n",
          "Files: ", unique_files
        )
      }
    })

    server_data_table_display(
      "combined_data_sample",
      reactive(combined_data_from_all_importers())
    )
    server_data_table_display(
      "combined_data_summary",
      reactive(combined_data_from_all_importers())
    )
    
    # Additional data table displays for new tabs
    server_data_table_display(
      "combined_data_file_info",
      reactive(combined_data_from_all_importers())
    )
    server_data_table_display(
      "combined_data_quality",
      reactive(combined_data_from_all_importers())
    )
    server_data_table_display(
      "combined_data_columns",
      reactive(combined_data_from_all_importers())
    )
    server_data_table_display(
      "combined_data_log",
      reactive(combined_data_from_all_importers())
    )

    list(
      df = reactive(combined_data_from_all_importers())
    )
  })
}

server_data_table_display <- function(id, input_data_reactive) {
  moduleServer(id, function(input, output, session) {
    processed_data <- reactiveVal()

    # Debounce the R code input to avoid rapid re-evaluations
    debounced_code_input <- debounce(reactive(input$code_input), 1000)

    observe({ # Unified observer for code changes or data changes
      current_code <- debounced_code_input()
      current_data <- input_data_reactive()
      
      # Don't req(current_data) here - our code templates handle NULL data gracefully
      
      # Only proceed if code is non-empty, otherwise might show raw data
      # Or, if code is empty, could default to showing current_data directly as DT
      if (is.null(current_code) || current_code == "") {
          # Default behavior if no code: render data as a basic DT table
          if (!is.null(current_data) && (is.data.frame(current_data) || is.data.table(current_data))) {
            processed_data(datatable(current_data, options = list(scrollX = TRUE, pageLength = 5, lengthMenu = c(5, 10, 25, 50)), rownames = FALSE, filter = 'top', class = 'compact stripe hover'))
          } else {
            # Handle NULL data case even when no custom code
            processed_data(data.frame(Message = "No data available") |> datatable(options = list(dom = 't', ordering = FALSE), rownames = FALSE, class = 'compact'))
          }
          return()
      }

      spsComps::shinyCatch({
        # Ensure 'df' is available in the eval environment
        env <- new.env(parent = .GlobalEnv) # Safer environment
        env$df <- current_data
        # Make essential functions available for data table display code
        env$datatable <- DT::datatable
        env$renderPrint <- shiny::renderPrint
        env$skim <- skimr::skim  # Use fully qualified name
        env$setDT <- data.table::setDT
        env$data.frame <- data.frame
        env$is.data.table <- data.table::is.data.table
        env$is.data.frame <- is.data.frame
        env$is.null <- is.null
        env$nrow <- nrow
        env$ncol <- ncol
        env$names <- names
        env$paste <- paste
        env$c <- c
        env$library <- library  # Allow library calls in eval code
        env$require <- require   # Allow require calls in eval code
        
        evaluated_output <- eval(parse(text = current_code), envir = env)
        processed_data(evaluated_output)
      })
    }) |> bindEvent(list(debounced_code_input(), input_data_reactive(), input$apply_code), ignoreNULL = FALSE, ignoreInit = FALSE)


    output$data_table <- renderUI({
      req(processed_data()) # Ensure processed_data is available
      tagList(processed_data()) # Wrap in tagList in case it's multiple UI elements
    })
  })
}

# --- Server Module for Plotter ---
server_plotter <- function(id, combined_data_reactive, main_session_input = NULL) { # Added main_session_input
  moduleServer(id, function(input, output, session) {
    ns <- session$ns

    # Tab renaming functionality for plotter (same as importer)
    plot_rename_trigger <- reactiveVal(NULL)

    # Auto-apply plot name when input changes (no button needed)
    observeEvent(input$plot_name, {
      req(input$plot_name)
      new_name <- trimws(input$plot_name)

      cat("=== PLOT NAME AUTO-APPLY ===\n")
      cat("Module ID:", id, "\n")
      cat("New name:", new_name, "\n")

      if (new_name == "") {
        return() # Don't apply empty names
      }

      # Set the reactive value to trigger parent's observer
      current_title <- id # Use the module ID as current title
      plot_rename_trigger(list(
        moduleId = id,
        newName = new_name,
        currentTitle = current_title,
        timestamp = Sys.time() # Ensure trigger fires even with same name
      ))

      cat("Plot rename trigger set automatically\n")
      showNotification(paste("Plot renamed to:", new_name), type = "message", duration = 2)
    }, ignoreInit = TRUE) # Only trigger on user changes, not initial load

    # --- Plot-specific Data Processing ---
    df_plot_data_processed <- reactiveVal()
    r_code_plot_process_reactive <- debounce(reactive(input$r_code_plot_process), 500)
    ace_server_functions(ns("r_code_plot_process"))

    # Multiple ace editors for different plot components
    r_code_plot_text_reactive <- debounce(reactive(input$r_code_plot_text), 500)
    r_code_plot_data_processing_reactive <- debounce(reactive(input$r_code_plot_data_processing), 500)
    r_code_plot_base_setup_reactive <- debounce(reactive(input$r_code_plot_base_setup), 500)
    r_code_plot_themes_styling_reactive <- debounce(reactive(input$r_code_plot_themes_styling), 500)
    r_code_plot_statistical_overlays_reactive <- debounce(reactive(input$r_code_plot_statistical_overlays), 500)
    r_code_plot_grid_axes_reactive <- debounce(reactive(input$r_code_plot_grid_axes), 500)
    r_code_plot_faceting_final_reactive <- debounce(reactive(input$r_code_plot_faceting_final), 500)
    r_code_plot_static_reactive <- debounce(reactive(input$r_code_plot_static), 500)
    r_code_plot_interactive_reactive <- debounce(reactive(input$r_code_plot_interactive), 500)
    r_code_plot_table_reactive <- debounce(reactive(input$r_code_plot_table), 500)
    r_code_plot_final_reactive <- debounce(reactive(input$r_code_plot_final), 500)

    # Ace server functions for each editor
    ace_server_functions(ns("r_code_plot_text"))
    ace_server_functions(ns("r_code_plot_data_processing"))
    ace_server_functions(ns("r_code_plot_base_setup"))
    ace_server_functions(ns("r_code_plot_themes_styling"))
    ace_server_functions(ns("r_code_plot_statistical_overlays"))
    ace_server_functions(ns("r_code_plot_grid_axes"))
    ace_server_functions(ns("r_code_plot_faceting_final"))
    ace_server_functions(ns("r_code_plot_static"))
    ace_server_functions(ns("r_code_plot_interactive"))
    ace_server_functions(ns("r_code_plot_table"))
    ace_server_functions(ns("r_code_plot_final"))

    # Update source filter checkboxes when combined data changes
    observe({
      req(combined_data_reactive())

      combined_df <- combined_data_reactive()
      if (!is.null(combined_df) && nrow(combined_df) > 0 && 'source_importer_id' %in% names(combined_df)) {
        # Get unique source IDs
        unique_sources <- unique(combined_df$source_importer_id)

        if (length(unique_sources) > 0) {
          # Create mapping from module IDs to friendly names
          source_labels <- sapply(unique_sources, function(source_id) {
            # Convert data_import_module_1 to "Data Import 1"
            if (grepl("^data_import_module_", source_id)) {
              tab_number <- gsub("data_import_module_", "", source_id)
              paste("Data Import", tab_number)
            } else {
              # Fallback for other naming patterns
              source_id
            }
          })

          # Generate checkboxes
          checkbox_list <- lapply(seq_along(unique_sources), function(i) {
            source_id <- unique_sources[i]
            label <- source_labels[i]

            div(class = "form-check form-check-inline",
              tags$input(
                type = "checkbox",
                class = "form-check-input",
                id = session$ns(paste0("source_", source_id)),
                value = source_id,
                checked = "checked" # Default to all sources selected
              ),
              tags$label(
                class = "form-check-label small",
                `for` = session$ns(paste0("source_", source_id)),
                label
              )
            )
          })

          # Update the UI
          output$source_filter_checkboxes <- renderUI({
            tagList(checkbox_list)
          })
        }
      } else {
        # No data available
        output$source_filter_checkboxes <- renderUI({
          p(class = "text-muted small", "No data sources available yet")
        })
      }
    })

    # Process data when button is clicked
    observeEvent(input$data_render, {
      req(combined_data_reactive())

      spsComps::shinyCatch({
        # Get the combined data
        combined_df <- data.table::copy(combined_data_reactive())

        if (is.null(combined_df) || nrow(combined_df) == 0) {
          showNotification("No data available to process", type = "warning")
          return()
        }

        # Apply source filtering
        if ('source_importer_id' %in% names(combined_df)) {
          # Get all unique sources
          unique_sources <- unique(combined_df$source_importer_id)

          # Check which sources are selected
          selected_sources <- c()
          for (source_id in unique_sources) {
            checkbox_id <- paste0("source_", source_id)
            if (!is.null(input[[checkbox_id]]) && input[[checkbox_id]]) {
              selected_sources <- c(selected_sources, source_id)
            }
          }

          # Filter data by selected sources
          if (length(selected_sources) > 0) {
            combined_df <- combined_df[combined_df$source_importer_id %in% selected_sources, ]
          } else {
            # If no sources selected, show warning and use all data
            showNotification("No sources selected - using all data", type = "warning")
          }
        }

        # Apply sampling if specified
        if (!is.null(input$sample_n_plot) && input$sample_n_plot > 0 && input$sample_n_plot < 1) {
          n_sample <- floor(nrow(combined_df) * input$sample_n_plot)
          if (n_sample > 0) {
            combined_df <- combined_df[sample(nrow(combined_df), n_sample), ]
          }
        }

        # Environment for plot data processing
        env <- new.env(parent = .GlobalEnv)
        env$df <- as.data.table(combined_df) # Start with filtered and sampled data
        env$input <- input # Expose module's input for filtering etc.
        env$filter_in <- filter_in
        env$filter_out <- filter_out
        env$rname <- rname
        env$str_extract <- stringr::str_extract
        env$sample <- sample # base R sample

        # Add code sharing functions
        env$get_plotter_code <- get_plotter_code
        env$get_importer_code <- get_importer_code
        env$main_session_input <- main_session_input

        processed_df <- eval(parse(text = r_code_plot_process_reactive()), envir = env)
        df_plot_data_processed(processed_df)

        # Update aesthetics choices based on processed data columns
        if (!is.null(processed_df) && ncol(processed_df) > 0) {
          column_choices <- c("None" = "null", setNames(names(processed_df), names(processed_df)))

          updateSelectInput(session, "plot_color", choices = column_choices, selected = if("series" %in% names(processed_df)) "series" else "null")
          updateSelectInput(session, "plot_linetype", choices = column_choices, selected = "null")
        }

        showNotification("Plot-specific data processing complete.", type = "message")
      })
    })

    # Display tables for plot data
    server_data_table_display(
      "plot_data_table_input_data",
      reactive(combined_data_reactive()) # Show the raw input to this plotter
    )
    server_data_table_display(
      "plot_data_table_input_data_summary",
      reactive(combined_data_reactive()) # Show summary of raw input data
    )
    server_data_table_display(
      "plot_data_table_modified_data",
      reactive(df_plot_data_processed()) # Show data after plot-specific processing
    )
    server_data_table_display(
      "plot_data_table_modified_data_summary",
      reactive(df_plot_data_processed()) # Summary of processed data
    )

    # --- Plot Rendering Logic ---
    plot_object_reactive <- reactiveVal(NULL)

    observeEvent(input$plot_render, {
      spsComps::shinyCatch({
        req(df_plot_data_processed()) # Requires data processed for *this* plot

        # Environment for plot generation
        env <- new.env(parent = .GlobalEnv)
        env$df <- df_plot_data_processed()
        env$input <- input # Expose module's input for titles, labels, etc.

        # Check if ggplot2 can load properly
        ggplot2_available <- tryCatch({
          # Test if we can actually load ggplot2 and its dependencies
          library(ggplot2, quietly = TRUE)
          # Test a simple ggplot operation
          test_plot <- ggplot(data.frame(x=1, y=1), aes(x=x, y=y)) + geom_point()
          TRUE
        }, error = function(e) {
          cat("ggplot2 loading failed:", e$message, "\n")
          FALSE
        })

        if (ggplot2_available) {
          # Add ggplot2 functions - full version
          env$ggplot <- ggplot2::ggplot
          env$geom_line <- ggplot2::geom_line
          env$geom_point <- ggplot2::geom_point
          env$geom_ribbon <- ggplot2::geom_ribbon
          env$aes <- ggplot2::aes
          env$labs <- ggplot2::labs
          env$ggtitle <- ggplot2::ggtitle # Deprecated, use labs(title=)
          env$xlab <- ggplot2::xlab # Deprecated
          env$ylab <- ggplot2::ylab # Deprecated
          env$theme_bw <- ggplot2::theme_bw
          env$facet_wrap <- ggplot2::facet_wrap
          env$ggplot2_available <- TRUE
        } else {
          # Create fallback functions that use base R plots or plotly
          env$ggplot2_available <- FALSE
          env$ggplot <- function(data = NULL, mapping = NULL, ...) {
            warning("ggplot2 not available - use plotly functions instead")
            return(list(data = data, mapping = mapping, type = "ggplot_fallback"))
          }
          env$geom_line <- function(...) {
            warning("ggplot2 not available - use plotly::add_lines() instead")
            return(list(type = "geom_fallback"))
          }
          env$geom_point <- function(...) {
            warning("ggplot2 not available - use plotly::add_markers() instead")
            return(list(type = "geom_fallback"))
          }
          env$geom_ribbon <- function(...) {
            warning("ggplot2 not available - use plotly::add_ribbons() instead")
            return(list(type = "geom_fallback"))
          }
          env$aes <- function(...) {
            warning("ggplot2 not available - define aesthetics directly in plotly")
            return(list(type = "aes_fallback"))
          }
          env$labs <- function(...) {
            warning("ggplot2 not available - use plotly::layout() instead")
            return(list(type = "labs_fallback"))
          }
          env$ggtitle <- function(...) {
            warning("ggplot2 not available - use plotly::layout(title = ...) instead")
            return(list(type = "title_fallback"))
          }
          env$xlab <- function(...) {
            warning("ggplot2 not available - use plotly::layout(xaxis = list(title = ...)) instead")
            return(list(type = "xlab_fallback"))
          }
          env$ylab <- function(...) {
            warning("ggplot2 not available - use plotly::layout(yaxis = list(title = ...)) instead")
            return(list(type = "ylab_fallback"))
          }
          env$theme_bw <- function(...) {
            warning("ggplot2 not available - use plotly styling instead")
            return(list(type = "theme_fallback"))
          }
          env$facet_wrap <- function(...) {
            warning("ggplot2 not available - create subplots with plotly::subplot() instead")
            return(list(type = "facet_fallback"))
          }
        }

        # Add scattermore with safe loading
        tryCatch({
          env$geom_scattermore <- scattermore::geom_scattermore
        }, error = function(e) {
          env$geom_scattermore <- function(...) {
            warning("scattermore not available - use plotly::add_markers() for large datasets")
            return(list(type = "scattermore_fallback"))
          }
        })

        env$str_sub <- stringr::str_sub
        env$ggplotly <- plotly::ggplotly
        env$plot_ly <- plotly::plot_ly
        env$add_trace <- plotly::add_trace
        env$add_lines <- plotly::add_lines
        env$add_markers <- plotly::add_markers
        env$add_ribbons <- plotly::add_ribbons
        env$layout <- plotly::layout
        env$renderPlot <- shiny::renderPlot # For static ggplot
        env$renderPlotly <- plotly::renderPlotly # For plotly/ggplotly
        env$renderPrint <- shiny::renderPrint # For text output
        env$renderUI <- shiny::renderUI # For flexible output
        env$datatable <- DT::datatable # For table output
        env$tagList <- shiny::tagList # For multiple UI elements
        env$div <- shiny::div # For HTML containers
        env$h3 <- shiny::h3 # For headings
        env$h4 <- shiny::h4 # For headings
        env$p <- shiny::p # For paragraphs
        env$copy <- data.table::copy # For data.table copying
        env$setDT <- data.table::setDT # For converting to data.table

        # Add code sharing functions
        env$get_plotter_code <- get_plotter_code
        env$get_importer_code <- get_importer_code
        env$main_session_input <- main_session_input

        # Add modular template variables for the combined static template to use
        env$ggplot_data_processing_template <- ggplot_data_processing_template
        env$ggplot_base_setup_template <- ggplot_base_setup_template
        env$ggplot_themes_styling_template <- ggplot_themes_styling_template
        env$ggplot_statistical_overlays_template <- ggplot_statistical_overlays_template
        env$ggplot_grid_axes_template <- ggplot_grid_axes_template
        env$ggplot_faceting_final_template <- ggplot_faceting_final_template

        # IMPORTANT: Override with user's ace editor inputs when available
        # This allows users to edit the modular templates in real-time
        if (!is.null(r_code_plot_data_processing_reactive()) && nzchar(r_code_plot_data_processing_reactive())) {
          env$ggplot_data_processing_template <- r_code_plot_data_processing_reactive()
        }
        if (!is.null(r_code_plot_base_setup_reactive()) && nzchar(r_code_plot_base_setup_reactive())) {
          env$ggplot_base_setup_template <- r_code_plot_base_setup_reactive()
        }
        if (!is.null(r_code_plot_themes_styling_reactive()) && nzchar(r_code_plot_themes_styling_reactive())) {
          env$ggplot_themes_styling_template <- r_code_plot_themes_styling_reactive()
        }
        if (!is.null(r_code_plot_statistical_overlays_reactive()) && nzchar(r_code_plot_statistical_overlays_reactive())) {
          env$ggplot_statistical_overlays_template <- r_code_plot_statistical_overlays_reactive()
        }
        if (!is.null(r_code_plot_grid_axes_reactive()) && nzchar(r_code_plot_grid_axes_reactive())) {
          env$ggplot_grid_axes_template <- r_code_plot_grid_axes_reactive()
        }
        if (!is.null(r_code_plot_faceting_final_reactive()) && nzchar(r_code_plot_faceting_final_reactive())) {
          env$ggplot_faceting_final_template <- r_code_plot_faceting_final_reactive()
        }

        # Combine all the code sections
        combined_code <- paste(
          "# Text code section:",
          paste("text_code <- '", gsub("'", "\\\\'", r_code_plot_text_reactive()), "'", sep=""),
          "",
          "# Static code section:",
          paste("static_code <- '", gsub("'", "\\\\'", r_code_plot_static_reactive()), "'", sep=""),
          "",
          "# Interactive code section:",
          paste("interactive_code <- '", gsub("'", "\\\\'", r_code_plot_interactive_reactive()), "'", sep=""),
          "",
          "# Table code section:",
          paste("table_code <- '", gsub("'", "\\\\'", r_code_plot_table_reactive()), "'", sep=""),
          "",
          "# Final conditional code:",
          r_code_plot_final_reactive(),
          sep = "\n"
        )

        plot_result <- eval(parse(text = combined_code), envir = env)
        plot_object_reactive(plot_result)
        showNotification("Plot generated successfully.", type = "message")
      })
    })

    # Flexible plot output using renderUI
    output$plot_output <- renderUI({
      req(plot_object_reactive())

      plot_obj <- plot_object_reactive()

      # Handle different types of output flexibly
      if (inherits(plot_obj, "htmlwidget")) {
        # Plotly or other HTML widgets
        plot_obj
      } else if (inherits(plot_obj, "ggplot")) {
        # ggplot objects - render as plot
        renderPlot({
          plot_obj
        }, height = 600)
      } else if (inherits(plot_obj, "datatables")) {
        # DataTable objects
        plot_obj
      } else if (is.list(plot_obj) && all(sapply(plot_obj, function(x) inherits(x, "shiny.tag") || inherits(x, "shiny.tag.list")))) {
        # List of UI elements
        tagList(plot_obj)
      } else if (inherits(plot_obj, "shiny.render.function")) {
        # Already a render function (renderPrint, etc.)
        plot_obj
      } else {
        # Fallback: try to render as print output
        renderPrint({
          plot_obj
        })
      }
    })

    # Download handler for plots
    output$download_output <- downloadHandler(
      filename = function() {
        plot_name <- if (!is.null(input$plot_name) && nzchar(input$plot_name)) {
          gsub("[^a-zA-Z0-9_.-]", "_", input$plot_name)
        } else {
          id
        }

        if (input$plot_type == "dynamic") {
          paste0(plot_name, "_", Sys.Date(), ".html")
        } else if (input$plot_type == "static") {
          paste0(plot_name, "_", Sys.Date(), ".png")
        } else {
          paste0(plot_name, "_", Sys.Date(), ".txt")
        }
      },
      content = function(file) {
        req(plot_object_reactive())

        plot_obj <- plot_object_reactive()

        if (input$plot_type == "dynamic" && inherits(plot_obj, "htmlwidget")) {
          htmlwidgets::saveWidget(plot_obj, file, selfcontained = TRUE)
        } else if (input$plot_type == "static" && inherits(plot_obj, "ggplot")) {
          ggsave(file, plot_obj, device = "png", width = 12, height = 8, dpi = 300)
        } else {
          # Fallback: save as text
          writeLines(capture.output(print(plot_obj)), file)
        }
      }
    )

    # Return the plot reactive and rename trigger for parent access
    list(
      plot = reactive(plot_object_reactive()),
      plot_rename_trigger = plot_rename_trigger
    )
  })
}

#' Data Plotter Server Function
#'
#' @param input Shiny input object
#' @param output Shiny output object
#' @param session Shiny session object
#' @export
server_global <- function(input, output, session) {
  options(shiny.maxRequestSize = 1000 * 1024^2) # 1GB
  
  # Initialize dark mode - only call this when session is available
  tryCatch({
    toggle_dark_mode(mode = "dark")
  }, error = function(e) {
    # Ignore errors if called outside of Shiny session
    cat("Note: toggle_dark_mode skipped (not in Shiny session)\n")
  })

  # JSON-Based Template Save/Load Logic
  safe_input_value <- function(val) {
    if (is.null(val)) {
      return(NULL)
    } else if (is.list(val) && length(val) > 1) {
      return(as.character(val))
    } else if (inherits(val, c("Date", "POSIXct", "POSIXlt"))) {
      return(as.character(val))
    } else if (is.logical(val) || is.numeric(val)) {
      return(val)
    } else {
      return(as.character(val))
    }
  }

  # Initialize download handler when session is available
  tryCatch({
    output$download_template <- downloadHandler(
      filename = function() {
        req(input$template_file_name)
        paste0(tools::file_path_sans_ext(input$template_file_name), ".json")
      },
      content = function(file) {
        tryCatch({
          all_inputs_list <- reactiveValuesToList(input)
          
          cat("=== JSON TEMPLATE SAVE ===\n")
          cat("Total inputs captured:", length(all_inputs_list), "\n")
          
          # Get current module counts
          current_importer_count <- isolate(importer_counter())
          current_plotter_count <- isolate(plotter_counter())
          
          # Split inputs by category
          general_inputs <- list()
          importer_inputs <- list()
          plotter_inputs <- list()
          ace_inputs <- list()
          
          for (input_name in names(all_inputs_list)) {
            val <- safe_input_value(all_inputs_list[[input_name]])
            
            if (grepl("r_code", input_name)) {
              ace_inputs[[input_name]] <- val
            } else if (grepl("data_import_module_[0-9]+", input_name)) {
              importer_inputs[[input_name]] <- val
            } else if (grepl("plotter_[0-9]+", input_name)) {
              plotter_inputs[[input_name]] <- val
            } else {
              general_inputs[[input_name]] <- val
            }
          }
          
          # Create structured template
          template_data <- list(
            metadata = list(
              template_version = "2.0",
              created_date = as.character(Sys.time()),
              importer_count = current_importer_count,
              plotter_count = current_plotter_count,
              total_inputs = length(all_inputs_list)
            ),
            modules = list(
              importers = current_importer_count,
              plotters = current_plotter_count
            ),
            inputs = list(
              general = general_inputs,
              importers = importer_inputs,
              plotters = plotter_inputs,
              ace_editors = ace_inputs
            )
          )
          
          # Write JSON file
          jsonlite::write_json(template_data, file, pretty = TRUE, auto_unbox = TRUE)
          
          cat("Saved:", length(general_inputs), "general,", length(importer_inputs), "importer,", 
              length(plotter_inputs), "plotter,", length(ace_inputs), "ace inputs\n")
          
          showNotification(paste("Template saved:", current_importer_count, "importers,", 
                                 current_plotter_count, "plotters"), type = "message")
          
        }, error = function(e) {
          showNotification(paste("Error saving template:", e$message), type = "error", duration = 10)
          # Create minimal JSON file to prevent download failure
          minimal_template <- list(
            metadata = list(template_version = "2.0", error = e$message),
            inputs = list()
          )
          jsonlite::write_json(minimal_template, file, pretty = TRUE, auto_unbox = TRUE)
        })
      }
    )
  }, error = function(e) {
    # Ignore errors if called outside of Shiny session
    cat("Note: downloadHandler skipped (not in Shiny session)\n")
  })

  observeEvent(input$template_upload, {
    req(input$template_upload)
    tryCatch({
      # Load JSON template
      template_data <- jsonlite::fromJSON(input$template_upload$datapath, simplifyVector = FALSE)
      
      cat("=== JSON TEMPLATE LOAD ===\n")
      cat("Template version:", if(!is.null(template_data$metadata$template_version)) template_data$metadata$template_version else "unknown", "\n")
      
      # Validate template structure
      if (is.null(template_data$metadata) || is.null(template_data$inputs)) {
        showNotification("Invalid JSON template file structure", type = "error", duration = 10)
        return()
      }
      
      # Extract module requirements
      importer_count_needed <- if(!is.null(template_data$modules$importers)) as.integer(template_data$modules$importers) else 1
      plotter_count_needed <- if(!is.null(template_data$modules$plotters)) as.integer(template_data$modules$plotters) else 0
      
      # Ensure valid counts
      importer_count_needed <- max(1, importer_count_needed)
      plotter_count_needed <- max(0, plotter_count_needed)
      
      cat("Modules needed - Importers:", importer_count_needed, "Plotters:", plotter_count_needed, "\n")
      
      # Get current counts
      current_importer_count <- isolate(importer_counter())
      current_plotter_count <- isolate(plotter_counter())
      
      cat("Current modules - Importers:", current_importer_count, "Plotters:", current_plotter_count, "\n")
      
      # STEP 1: Create required importer modules
      if (current_importer_count < importer_count_needed) {
        tabs_to_create <- importer_count_needed - current_importer_count
        cat("Creating", tabs_to_create, "additional importer tabs\n")
        
        for (i in 1:tabs_to_create) {
          current_count <- isolate(importer_counter()) + 1
          importer_counter(current_count)
          import_id <- paste0("data_import_module_", current_count)

          nav_insert(
            id = "mainmenu",
            target = "Combined Data",
            position = "before",
            nav = nav_panel(
              title = paste("Import", current_count),
              ui_data_importer(import_id)
            ),
            session = session
          )
          
          # Initialize server module
          importer_module_output <- server_data_import(import_id, global_files)
          current_instances <- importer_instances()
          current_instances[[import_id]] <- importer_module_output
          importer_instances(current_instances)
          
          # Observe tab rename trigger from this importer module
          observeEvent(importer_module_output$tab_rename_trigger(), {
            req(importer_module_output$tab_rename_trigger())
            
            rename_data <- importer_module_output$tab_rename_trigger()
            cat("Importer tab rename triggered:", rename_data$newName, "\n")
            
            # Send message to JavaScript to update the tab
            session$sendCustomMessage("updateNavTabTitle", list(
              moduleId = rename_data$moduleId,
              newTitle = rename_data$newName,
              oldTitle = rename_data$currentTitle
            ))
          })
          
          cat("Created importer:", import_id, "\n")
        }
      }
      
      # STEP 2: Create required plotter modules
      if (current_plotter_count < plotter_count_needed) {
        tabs_to_create <- plotter_count_needed - current_plotter_count
        cat("Creating", tabs_to_create, "additional plotter tabs\n")
        
        for (i in 1:tabs_to_create) {
          current_count <- isolate(plotter_counter()) + 1
          plotter_counter(current_count)
          plot_id <- paste0("plotter_", current_count)

          nav_insert(
            id = "mainmenu",
            target = "Analysis",
            position = "before",
            nav = nav_panel(
              title = plot_id,
              ui_plotter(plot_id)
            ),
            session = session
          )
          
          # Initialize server module
          plotter_module_output <- server_plotter(plot_id, data_combiner$df, input)
          current_instances <- plotter_instances()
          current_instances[[plot_id]] <- plotter_module_output
          plotter_instances(current_instances)
          
          # Observe plot rename trigger from this plotter module
          observeEvent(plotter_module_output$plot_rename_trigger(), {
            req(plotter_module_output$plot_rename_trigger())
            
            rename_data <- plotter_module_output$plot_rename_trigger()
            cat("Plotter tab rename triggered:", rename_data$newName, "\n")
            
            # Send message to JavaScript to update the tab
            session$sendCustomMessage("updateNavTabTitle", list(
              moduleId = rename_data$moduleId,
              newTitle = rename_data$newName,
              oldTitle = rename_data$currentTitle
            ))
          })
          
          cat("Created plotter:", plot_id, "\n")
        }
      }
      
      # STEP 3: Apply inputs in order (modules are now ready)
      cat("Applying template inputs...\n")
      
      # Helper function to safely update inputs
      safe_update_input <- function(input_id, value) {
        if (is.null(value)) return(FALSE)
        
        tryCatch({
          current_val <- isolate(input[[input_id]])
          
          if (is.null(current_val)) {
            cat("  Input", input_id, "not found, skipping\n")
            return(FALSE)
          }
          
          # Convert value based on current input type
          if (is.logical(current_val)) {
            updateCheckboxInput(session, input_id, value = as.logical(value))
          } else if (is.numeric(current_val)) {
            updateNumericInput(session, input_id, value = as.numeric(value))
          } else if (inherits(current_val, "Date")) {
            updateDateInput(session, input_id, value = as.Date(value))
          } else if (is.character(value) && length(value) > 1) {
            # Multiple values for selectize
            updateSelectizeInput(session, input_id, selected = value)
          } else {
            # Try select first, then text
            tryCatch({
              updateSelectInput(session, input_id, selected = as.character(value))
            }, error = function(e) {
              updateTextInput(session, input_id, value = as.character(value))
            })
          }
          
          cat("  Updated", input_id, "=", paste(value, collapse=","), "\n")
          return(TRUE)
        }, error = function(e) {
          cat("  Failed to update", input_id, ":", e$message, "\n")
          return(FALSE)
        })
      }
      
      # Apply general inputs first
      if (!is.null(template_data$inputs$general)) {
        cat("Applying", length(template_data$inputs$general), "general inputs\n")
        for (input_id in names(template_data$inputs$general)) {
          safe_update_input(input_id, template_data$inputs$general[[input_id]])
        }
      }
      
      # Apply importer inputs
      if (!is.null(template_data$inputs$importers)) {
        cat("Applying", length(template_data$inputs$importers), "importer inputs\n")
        for (input_id in names(template_data$inputs$importers)) {
          safe_update_input(input_id, template_data$inputs$importers[[input_id]])
        }
      }
      
      # Apply ace editor inputs
      if (!is.null(template_data$inputs$ace_editors)) {
        cat("Applying", length(template_data$inputs$ace_editors), "ace editor inputs\n")
        for (ace_id in names(template_data$inputs$ace_editors)) {
          ace_value <- template_data$inputs$ace_editors[[ace_id]]
          if (!is.null(ace_value) && nzchar(ace_value)) {
            tryCatch({
              updateAceEditor(session, ace_id, value = as.character(ace_value))
              cat("  Updated ace editor:", ace_id, "\n")
            }, error = function(e) {
              cat("  Failed to update ace editor", ace_id, ":", e$message, "\n")
            })
          }
        }
      }
      
      # Apply plotter inputs (these should work now that modules exist)
      if (!is.null(template_data$inputs$plotters)) {
        cat("Applying", length(template_data$inputs$plotters), "plotter inputs\n")
        successful_count <- 0
        
        for (input_id in names(template_data$inputs$plotters)) {
          if (safe_update_input(input_id, template_data$inputs$plotters[[input_id]])) {
            successful_count <- successful_count + 1
          }
        }
        
        cat("Successfully applied", successful_count, "out of", length(template_data$inputs$plotters), "plotter inputs\n")
      }
      
      showNotification(paste("Template loaded successfully:", importer_count_needed, "importers,", 
                             plotter_count_needed, "plotters"), type = "message")
      
    }, error = function(e) {
      showNotification(paste("Failed to load template:", e$message), type = "error", duration = 10)
      cat("Template load error:", e$message, "\n")
    })
  })

  # Global File Management for Input Data Tab
  global_files <- reactiveVal(list())
  
  # File upload handler for individual files
  observeEvent(input$global_file_upload, {
    req(input$global_file_upload)
    
    current_files <- global_files()
    new_files <- input$global_file_upload
    
    for (i in 1:nrow(new_files)) {
      file_name <- new_files$name[i]
      file_path <- new_files$datapath[i]
      
      # Check if file already exists
      if (file_name %in% names(current_files) && !input$overwrite_files) {
        showNotification(
          paste("File", file_name, "already exists. Enable overwrite to replace it."),
          type = "warning"
        )
        next
      }
      
      # Store file info
      current_files[[file_name]] <- list(
        name = file_name,
        path = file_path,
        size = file.info(file_path)$size,
        uploaded = Sys.time(),
        type = tools::file_ext(file_name),
        upload_type = "individual"
      )
    }
    
    global_files(current_files)
    showNotification(paste("Uploaded", nrow(new_files), "individual file(s)"), type = "message")
  })
  
  # Reactive values to track batched folder upload
  folder_upload_state <- reactiveValues(
    total_files_expected = 0,
    files_processed = 0,
    files_skipped = 0,
    files_filtered = 0,
    upload_in_progress = FALSE,
    batches_received = 0,
    total_batches = 0
  )
  
  # Batched folder upload handler - receives file data from JavaScript in chunks
  observeEvent(input$global_folder_upload_files_batch, {
    req(input$global_folder_upload_files_batch)
    
    batch_data <- input$global_folder_upload_files_batch
    batch_number <- batch_data$batchNumber
    is_last_batch <- batch_data$isLastBatch
    total_batches <- batch_data$totalBatches
    
    cat("=== FOLDER UPLOAD BATCH", batch_number + 1, "of", total_batches, "RECEIVED ===\n")
    cat("Number of files in this batch:", length(batch_data$files), "\n")
    cat("Is last batch:", is_last_batch, "\n")
    
    # Initialize or update upload state
    if (batch_number == 0) {
      folder_upload_state$upload_in_progress <- TRUE
      folder_upload_state$total_batches <- total_batches
      folder_upload_state$batches_received <- 0
      cat("Starting new folder upload session\n")
    }
    
    folder_upload_state$batches_received <- folder_upload_state$batches_received + 1
    
    current_files <- global_files()
    batch_files_processed <- 0
    batch_files_skipped <- 0
    
    # Process each file in this batch
    for (file_info in batch_data$files) {
      file_name <- file_info$name
      data_url <- file_info$dataURL
      original_path <- file_info$originalPath
      
      cat("Processing folder file:", file_name, "(original:", original_path, ")\n")
      
      # Check if file already exists
      if (file_name %in% names(current_files) && !input$overwrite_files) {
        cat("File already exists, skipping:", file_name, "\n")
        batch_files_skipped <- batch_files_skipped + 1
        next
      }
      
      # Decode the data URL and save to temp file
      tryCatch({
        # Remove data URL prefix
        data_part <- sub("^data:[^,]*,", "", data_url)
        
        # Decode base64
        file_content <- base64enc::base64decode(data_part)
        
        # Create temporary file
        temp_file <- tempfile(fileext = paste0(".", tools::file_ext(file_name)))
        writeBin(file_content, temp_file)
        
        # Store file info
        current_files[[file_name]] <- list(
          name = file_name,
          path = temp_file,
          size = length(file_content),
          uploaded = Sys.time(),
          type = tools::file_ext(file_name),
          upload_type = "folder",
          original_path = original_path
        )
        
        batch_files_processed <- batch_files_processed + 1
        cat("Successfully processed:", file_name, "\n")
        
      }, error = function(e) {
        cat("Error processing file", file_name, ":", e$message, "\n")
        showNotification(
          paste("Error processing", file_name, ":", e$message),
          type = "error"
        )
      })
    }
    
    # Update cumulative counters
    folder_upload_state$files_processed <- folder_upload_state$files_processed + batch_files_processed
    folder_upload_state$files_skipped <- folder_upload_state$files_skipped + batch_files_skipped
    
    global_files(current_files)
    
    # Show progress notification
    if (!is_last_batch) {
      showNotification(
        paste("Processed batch", batch_number + 1, "of", total_batches, 
              "(", batch_files_processed, "files in this batch)"),
        type = "message", duration = 2
      )
    } else {
      # Final batch - show summary
      folder_upload_state$upload_in_progress <- FALSE
      
      summary_msg <- paste("Folder upload complete:", 
                           folder_upload_state$files_processed, "files processed")
      if (folder_upload_state$files_skipped > 0) {
        summary_msg <- paste0(summary_msg, ", ", folder_upload_state$files_skipped, 
                             " files skipped (already exist)")
      }
      
      showNotification(summary_msg, type = "message", duration = 5)
      cat("Folder upload complete:", folder_upload_state$files_processed, "processed,", 
          folder_upload_state$files_skipped, "skipped across", total_batches, "batches\n")
    }
    
    cat("Batch", batch_number + 1, "complete:", batch_files_processed, "processed,", 
        batch_files_skipped, "skipped\n")
  })
  
  # Remove selected files
  observeEvent(input$remove_selected_files, {
    req(input$global_file_list_rows_selected)
    
    current_files <- global_files()
    selected_indices <- input$global_file_list_rows_selected
    file_names <- names(current_files)
    
    if (length(selected_indices) > 0 && length(file_names) >= max(selected_indices)) {
      files_to_remove <- file_names[selected_indices]
      for (file_name in files_to_remove) {
        current_files[[file_name]] <- NULL
      }
      global_files(current_files)
      showNotification(paste("Removed", length(files_to_remove), "file(s)"), type = "message")
    }
  })
  
  # Clear all files
  observeEvent(input$clear_all_files, {
    global_files(list())
    showNotification("All files cleared", type = "message")
  })
  
  # File list display
  tryCatch({
    output$global_file_list <- DT::renderDT({
      current_files <- global_files()
      
      if (length(current_files) == 0) {
        data.frame(
          Name = character(0),
          Type = character(0),
          Size = character(0),
          Source = character(0),
          Original_Path = character(0),
          Uploaded = character(0)
        )
      } else {
        file_df <- data.frame(
          Name = names(current_files),
          Type = sapply(current_files, function(x) x$type),
          Size = sapply(current_files, function(x) {
            size_mb <- round(x$size / 1024^2, 2)
            paste(size_mb, "MB")
          }),
          Source = sapply(current_files, function(x) {
            if (!is.null(x$upload_type)) {
              if (x$upload_type == "folder") " Folder" else " Individual"
            } else {
              " Individual"  # Default for existing files
            }
          }),
          Original_Path = sapply(current_files, function(x) {
            if (!is.null(x$original_path)) {
              x$original_path
            } else {
              x$name  # Fallback to file name
            }
          }),
          Uploaded = sapply(current_files, function(x) {
            format(x$uploaded, "%Y-%m-%d %H:%M:%S")
          }),
          stringsAsFactors = FALSE
        )
        
        DT::datatable(
          file_df,
          options = list(
            pageLength = 15,
            scrollX = TRUE,
            searching = TRUE,
            ordering = TRUE,
            columnDefs = list(
              list(width = '200px', targets = 0),  # Name column
              list(width = '300px', targets = 4)   # Original_Path column
            )
          ),
          selection = "multiple",
          rownames = FALSE
        )
      }
    })
  }, error = function(e) {
    cat("Note: global_file_list output skipped (not in Shiny session)\n")
  })
  
  # File statistics
  tryCatch({
    output$file_stats <- renderText({
      current_files <- global_files()
      
      if (length(current_files) == 0) {
        "No files uploaded"
      } else {
        total_size <- sum(sapply(current_files, function(x) x$size))
        total_size_mb <- round(total_size / 1024^2, 2)
        
        file_types <- table(sapply(current_files, function(x) x$type))
        type_summary <- paste(names(file_types), ":", file_types, collapse = ", ")
        
        paste(
          "Total files:", length(current_files), "\n",
          "Total size:", total_size_mb, "MB\n",
          "File types:", type_summary
        )
      }
    })
  }, error = function(e) {
    cat("Note: file_stats output skipped (not in Shiny session)\n")
  })

  # Dynamic Plotter Management
  plotter_instances <- reactiveVal(list())
  plotter_counter <- reactiveVal(0)

  # Dynamic Data Import Management
  importer_instances <- reactiveVal(list())
  importer_counter <- reactiveVal(0)
  
  # Store custom tab names for importers
  importer_tab_names <- reactiveVal(list())
  # Also store current displayed titles to help with repeated renames
  importer_current_titles <- reactiveVal(list())

  # Data Import and Combination Logic
  # Create reactive list of data frames from all active importers
  list_of_df_reactives_for_combiner <- reactive({
    current_importers <- importer_instances()
    if (length(current_importers) == 0) {
      return(list())
    }
    
    # Extract the 'df' reactive from each importer instance
    df_reactives <- lapply(current_importers, function(instance) {
      if (!is.null(instance) && "df" %in% names(instance)) {
        return(instance$df)
      }
      return(NULL)
    })
    
    # Filter out any NULL values
    Filter(Negate(is.null), df_reactives)
  })
  
  # Initialize data combiner module
  data_combiner <- tryCatch({
    server_data_combiner(
      "combiner", 
      list_of_df_reactives_for_combiner
    )
  }, error = function(e) {
    cat("Note: data_combiner module skipped (not in Shiny session)\n")
    # Return a dummy reactive for testing
    reactive({ NULL })
  })

  # Dynamic Data Import Creation
  observeEvent(input$insert_importer, {
    current_count <- isolate(importer_counter()) + 1
    importer_counter(current_count)
    import_id <- paste0("data_import_module_", current_count)

    # Check if there's a custom name for this tab
    current_names <- importer_tab_names()
    tab_title <- if (!is.null(current_names[[import_id]])) {
      current_names[[import_id]]
    } else {
      paste("Import", current_count)
    }

    nav_insert(
      id = "mainmenu",
      target = "Combined Data",
      position = "before",
      nav = nav_panel(
        title = tab_title,
        ui_data_importer(import_id)
      ),
      session = session
    )
    
    # Call the server module for the new importer, passing global files
    importer_module_output <- server_data_import(import_id, global_files)
    
    # Store the module's output
    current_instances <- importer_instances()
    current_instances[[import_id]] <- importer_module_output
    importer_instances(current_instances)
    
    # Observe tab rename trigger from this importer module
    observeEvent(importer_module_output$tab_rename_trigger(), {
      req(importer_module_output$tab_rename_trigger())
      
      rename_data <- importer_module_output$tab_rename_trigger()
      cat("Manual importer tab rename triggered:", rename_data$newName, "\n")
      
      # Send message to JavaScript to update the tab
      session$sendCustomMessage("updateNavTabTitle", list(
        moduleId = rename_data$moduleId,
        newTitle = rename_data$newName,
        oldTitle = rename_data$currentTitle
      ))
    })
    
    showNotification(
      paste("Added data import tab:", current_count), 
      type = "message"
    )
  })

  # Initialize with one default data import tab
  observeEvent(session$clientData, {
    if (isolate(importer_counter()) == 0) {
      # Trigger the insert_importer logic programmatically
      current_count <- 1
      importer_counter(current_count)
      import_id <- paste0("data_import_module_", current_count)

      # Check for custom name
      current_names <- importer_tab_names()
      tab_title <- if (!is.null(current_names[[import_id]])) {
        current_names[[import_id]]
      } else {
        paste("Import", current_count)
      }

      nav_insert(
        id = "mainmenu",
        target = "Combined Data",
        position = "before",
        nav = nav_panel(
          title = tab_title,
          ui_data_importer(import_id)
        ),
        session = session
      )
      
      # Call the server module for the initial importer, passing global files
      importer_module_output <- server_data_import(import_id, global_files)
      
      # Store the module's output
      current_instances <- list()
      current_instances[[import_id]] <- importer_module_output
      importer_instances(current_instances)
      
      # Observe tab rename trigger from this default importer module
      observeEvent(importer_module_output$tab_rename_trigger(), {
        req(importer_module_output$tab_rename_trigger())
        
        rename_data <- importer_module_output$tab_rename_trigger()
        cat("Default importer tab rename triggered:", rename_data$newName, "\n")
        
        # Send message to JavaScript to update the tab
        session$sendCustomMessage("updateNavTabTitle", list(
          moduleId = rename_data$moduleId,
          newTitle = rename_data$newName,
          oldTitle = rename_data$currentTitle
        ))
      })
    }
  }, once = TRUE)

  # Dynamic Plotter Creation
  observeEvent(input$insert_plot, {
    current_count <- isolate(plotter_counter()) + 1
    plotter_counter(current_count)
    plot_id <- paste0("plotter_", current_count)

    nav_insert(
      id = "mainmenu",
      target = "Analysis",
      position = "before",
      nav = nav_panel(
        title = plot_id,
        ui_plotter(plot_id)
      ),
      session = session
    )
    
    # Call the server module for the new plotter
    plotter_module_output <- server_plotter(plot_id, data_combiner$df, input)
    
    # Store the module's output
    current_instances <- plotter_instances()
    current_instances[[plot_id]] <- plotter_module_output
    plotter_instances(current_instances)
    
    # Observe plot rename trigger from this plotter module
    observeEvent(plotter_module_output$plot_rename_trigger(), {
      req(plotter_module_output$plot_rename_trigger())
      
      rename_data <- plotter_module_output$plot_rename_trigger()
      cat("Manual plotter tab rename triggered:", rename_data$newName, "\n")
      
      # Send message to JavaScript to update the tab
      session$sendCustomMessage("updateNavTabTitle", list(
        moduleId = rename_data$moduleId,
        newTitle = rename_data$newName,
        oldTitle = rename_data$currentTitle
      ))
    })
    
    showNotification(paste("Added plotter tab:", plot_id), type = "message")
  })

  # Helper & Downloader R Code Execution
  ace_server_functions("helper_input")
  observeEvent(input$helper_input, {
    output$helper_output <- renderUI({
      spsComps::shinyCatch({
        eval(
          parse(text = input$helper_input), 
          envir = new.env(parent = globalenv())
        )
      })
    })
  }, ignoreInit = FALSE, ignoreNULL = FALSE)

  ace_server_functions("downloader_input")
  observeEvent(input$downloader_input, {
    output$downloader_output <- renderUI({
      spsComps::shinyCatch({
        # Prepare the list of plot reactives for the downloader code
        plots_to_download_map <- lapply(plotter_instances(), function(instance) {
          if (!is.null(instance) && "plot" %in% names(instance) && 
              is.function(instance$plot)) {
            return(instance$plot)
          }
          return(NULL)
        })
        plots_to_download_map <- Filter(Negate(is.null), plots_to_download_map)

        # Environment for downloader code
        downloader_env <- new.env(parent = globalenv())
        downloader_env$dynamic_plots_map <- plots_to_download_map

        eval(parse(text = input$downloader_input), envir = downloader_env)
      })
    })
  }, ignoreInit = FALSE, ignoreNULL = FALSE)
}

